import { Component } from '@angular/core';
import { LanguageServiceService } from '../language-service.service';

@Component({
  selector: 'app-language',
  template: `
    <select [(ngModel)]="selectedLanguage" (change)="">
      <option value="en" (click)="">English</option>
      <option value="fr">French</option>
    </select>
  `
})
export class LanguageComponentComponent {
 selectedLanguage = 'en';

  constructor(private languageService: LanguageServiceService) { }

  changeLanguage() {
    this.languageService.changeLanguage(this.selectedLanguage).subscribe(response => {
      // Handle success
      console.log(response);
    }, error => {
      // Handle error
      console.error(error);
    });
  }
}
